var link=document.querySelector(".contacts-btn"),popup=document.querySelector(".feedback"),close=document.querySelector(".close-btn"),form=document.querySelector(".feedback-form"),fullname=document.querySelector("[name=fullname]"),email=document.querySelector("[name=email]"),message=document.querySelector("[name=message]"),storage=localStorage.getItem("fullname");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("feedback-show"),storage?(fullname.value=storage,message.focus()):fullname.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("feedback-show"),popup.classList.remove("error")}),form.addEventListener("submit",function(a){fullname.classList.remove("error"),email.classList.remove("error"),fullname.value&&email.value?localStorage.setItem("fullname",fullname.value):(a.preventDefault(),popup.classList.add("error"),fullname.value||fullname.classList.add("error"),email.value||email.classList.add("error"))});
